<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class Model_about_gallery extends CI_Model {    function __construct()    {        parent::__construct();    }		function getListGallery($about_id,$cond = null){		 $query	 = "SELECT a.about_gallery_id, a.about_id,  a.about_gallery_title,a.about_gallery_image,"                                    . " a.about_gallery_active_status,"                                    . "a.about_gallery_order,"                                    . "DATE_FORMAT( a.about_gallery_create_date, '%d-%m-%Y %H:%i:%s' ) as about_gallery_create_date, a.about_gallery_create_by "                                    . "FROM tbl_about_gallery as a "                                    . " inner join tbl_about as b on a.about_id = b.about_id "                                    . " where a.about_id=".$about_id." "                                    . " ".$cond;		$query		= $this->db->query($query)->result_array();		return $query;	}	    function getTitle($about_gallery_id){            $data = array();             $hasil =   $this->db->query("select material_title as title from tbl_about_gallery where about_gallery_id = $about_gallery_id ");				$data = $hasil->row_array();                                               return $data['title'];         }		function getGallery($id = '')	{		$where = '';		if($id != ''){			$where = "WHERE about_gallery_id = ".$id;		}		$sql	= "SELECT a.about_gallery_id, a.about_gallery_title,a.about_gallery_image,"                                    . "   a.about_gallery_active_status,"                                    . "a.about_gallery_order,"                                    . "DATE_FORMAT( a.about_gallery_create_date, '%d-%m-%Y %H:%i:%s' ) as about_gallery_create_date, a.about_gallery_create_by, "                                    . " b.about_id "                                    . "FROM tbl_about_gallery as a "                                    . " inner join tbl_about as b on a.about_id = b.about_id "                                    . " $where "                                    . " ORDER BY about_gallery_id ASC";			$query	= $this->db->query($sql);		$rs		= $query->result_array(); 				return $rs;		}		function activeGallery($id)	{		 $sql	= "UPDATE tbl_about_gallery SET about_gallery_active_status = abs(about_gallery_active_status-1),  				   about_gallery_update_date = now(), 				   about_gallery_update_by = ".$_SESSION['admin_data']['user_id']."				   WHERE about_gallery_id = ".$id;			$query	= $this->db->query($sql);				return $query;	}		function deleteGallery($id = '')	{		$sql	= "DELETE FROM tbl_about_gallery WHERE about_id = ".$id;			$query	= $this->db->query($sql);				$str = $this->db->last_query();				return $str;	}	function deleteGalleryby($id = '')	{		$sql	= "DELETE FROM tbl_about_gallery WHERE about_gallery_id = ".$id;			$query	= $this->db->query($sql);				$str = $this->db->last_query();				return $str;	}	function checkGallery($about_gallery_title, $about_gallery_id){		$sql	= "SELECT about_gallery_title FROM tbl_about_gallery WHERE about_gallery_title = '".$about_gallery_title."' and about_gallery_id = '".$about_gallery_id."'";		$query	= $this->db->query($sql);		$rs		= $query->result_array(); 		return $rs;	}	            function insertGallery($data){                foreach ($data as $value) {                  $this->db->insert('tbl_about_gallery', $value);                   }                $this->db->close();	}       	function editGallery($about_gallery_title,$about_gallery_image,$about_gallery_order,$gallery_active_status,$about_gallery_id){          $today= date("Y-m-d H:i:s");          $data = array(                        'about_gallery_title'=>$about_gallery_title,                        'about_gallery_image'=>$about_gallery_image,                        'about_gallery_order'=>$about_gallery_order,                        'about_gallery_active_status'=>$gallery_active_status,                        'about_gallery_update_by'=> $_SESSION['admin_data']['user_id'],                         'about_gallery_update_date'=> $today                        );            $this->db->where('about_gallery_id', $about_gallery_id);            $this->db->update('tbl_about_gallery', $data);            $this->db->close();	        }     function updateOrderGallery($id,$order)	{		$sql	= "UPDATE tbl_about_gallery SET 				   about_gallery_order= ".$order.",				   about_gallery_update_by = ".$_SESSION['admin_data']['user_id'].", about_gallery_update_date=now() WHERE about_gallery_id = ".$id;			$query	= $this->db->query($sql);		return $query;	}             }