<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class Model_escape extends CI_Model {    function __construct()    {        parent::__construct();    }        function getModule($module_name){            $data = array();            $sql="select module_id , module_group_id from tbl_module where module_path='".$module_name."' ";         		 $hasil = $this->db->query($sql);                        if($hasil->num_rows() > 0){				$data = $hasil->result();			}                        else{                            $data = '';                        }			$hasil->free_result();                        $this->db->close();			return $data;                 }         function getListEscape($cond = null){		  $query	    = "SELECT  a.escape_id,  a.escape_title, a.escape_image, a.escape_desc,a.escape_order,"                                    . " a.escape_active_status,"                                    . "DATE_FORMAT( a.escape_create_date, '%d-%m-%Y %H:%i:%s' ) as escape_create_date, a.escape_create_by "                                                                      . "FROM tbl_escape as a "                                    . " ".$cond;		$query		= $this->db->query($query)->result_array();		return $query;	}         		function getListEscapeAlias(){		 $query		= "SELECT escape_id, escape_title, escape_image, escape_desc,						DATE_FORMAT( escape_create_date, '%d %M %Y' ) as escape_create_date,						c.web_alias, c.web_ori						FROM tbl_escape a						INNER JOIN tbl_alias c ON a.escape_id = c.key_id 						WHERE a.escape_active_status = 1 AND c.alias_category = 'escape' AND c.alias_active_status = 1						ORDER BY escape_id DESC";		$query		= $this->db->query($query)->result_array();		return $query;	}		function getEscape($id = '')	{		$where = '';		if($id != ''){			$where = "WHERE escape_id = ".$id;		}		 $sql	= "SELECT a.escape_id,a.escape_title,a.escape_image, a.escape_active_status,a.escape_desc, "                                    . "DATE_FORMAT( a.escape_create_date, '%d-%m-%Y %H:%i:%s' ) as escape_create_date, a.escape_create_by "                                                                      . "FROM tbl_escape as a "                                    . " $where "                                    . " ORDER BY escape_id ASC";			$query	= $this->db->query($sql);		$rs		= $query->result_array(); 		return $rs;		}	function checkEscape($escape_title){		$sql	= "SELECT escape_title FROM tbl_escape WHERE escape_title = '".$escape_title."'";		$query	= $this->db->query($sql);		$rs		= $query->result_array(); 		return $rs;	}	function activeEscape($id)	{		$sql	= "UPDATE tbl_escape SET escape_active_status = abs(escape_active_status-1),  				   escape_update_date = now(), 				   escape_update_by = ".$_SESSION['admin_data']['user_id']."				   WHERE escape_id = ".$id;			$query	= $this->db->query($sql);				return $query;	}	function deleteEscape($id = '')	{		$sql	= "DELETE FROM tbl_escape WHERE escape_id = ".$id;			$query	= $this->db->query($sql);				$str = $this->db->last_query();				return $str;	}        function insertEscape($escape_title, $escape_imageurl,$escape_desc)	{		$sql	= "INSERT INTO tbl_escape SET                             escape_title='".$escape_title."',                            escape_image='".$escape_imageurl."',                            escape_desc='".$escape_desc."',                            escape_create_by = ".$_SESSION['admin_data']['user_id'].", escape_create_date = now()";					$query  = $this->db->query($sql);		$last_id  = $this->db->insert_id();		return $last_id;	}	function updateEscape($id,$escape_title, $escape_imageurl,$escape_desc)	{		 $sql	= "UPDATE tbl_escape SET                             escape_title='".$escape_title."',                            escape_image='".$escape_imageurl."',                            escape_desc='".$escape_desc."',                            escape_update_by = ".$_SESSION['admin_data']['user_id'].",                             escape_update_date=now() WHERE escape_id = ".$id;			$query	= $this->db->query($sql);		return $query;	}         function updateOrderEscape($id,$order)	{		$sql	= "UPDATE tbl_escape SET 				   escape_order= ".$order.",				   escape_update_by = ".$_SESSION['admin_data']['user_id'].", escape_update_date=now() WHERE escape_id = ".$id;			$query	= $this->db->query($sql);				return $query;	}         function getPageView(){         $data = array();         $sql="select * from tbl_page_view order by page_type asc";                  $hasil = $this->db->query($sql);                        if($hasil->num_rows() > 0){				$data = $hasil->result();			}                        else{                            $data = '';                        }			$hasil->free_result();                        $this->db->close();			return $data;              }          function GenerateEscape($cond = null){            $query		 = "SELECT  a.escape_id,  a.escape_title,  a.escape_image, a.escape_desc,a.escape_order, "                                    . "a.escape_active_status "                                                                    . "FROM tbl_escape as a "                                    . " ".$cond;                    $query = $this->db->query($query)->result_array();                    $data  = array();                    foreach ($query as $row) {                    $data[] = array(                    'escape_id' => $row['escape_id'],                    'escape_title' => $row['escape_title'],                    'escape_name' => $row['escape_name'],                    'escape_image' => $row['escape_image'],                    'escape_desc' => $row['escape_desc'],                    'escape_order' => $row['escape_order'],                    'escape_urla' => $row['escape_urla'],                    'escape_urlb' => $row['escape_urlb']                );            }            //$query->free_result();            $this->db->close();            return $data;	} function getMenu($escape_id){		 $query	 = "SELECT a.escape_menu_id, a.escape_menu_title,a.escape_menu_desc,"                                    . " a.escape_menu_active_status,"                                    . "a.escape_menu_order "                                    . "FROM tbl_escape_menu as a "                                    . " inner join tbl_escape as b on a.escape_id = b.escape_id "                                    . " where a.escape_menu_active_status =1 and a.escape_id =".$escape_id." order by a.escape_menu_order ASC";                                    		$query		= $this->db->query($query)->result_array();		return $query;	}     }