<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class Model_kohsamui extends CI_Model {    function __construct()    {        parent::__construct();    }        function getModule($module_name){            $data = array();            $sql="select module_id , module_group_id from tbl_module where module_path='".$module_name."' ";         		 $hasil = $this->db->query($sql);                        if($hasil->num_rows() > 0){				$data = $hasil->result();			}                        else{                            $data = '';                        }			$hasil->free_result();                        $this->db->close();			return $data;                 }         function getListKohsamui($cond = null){		  $query	    = "SELECT  a.kohsamui_id,  a.kohsamui_title, a.kohsamui_image, a.kohsamui_desc,a.kohsamui_order,"                                    . " a.kohsamui_active_status,"                                    . "DATE_FORMAT( a.kohsamui_create_date, '%d-%m-%Y %H:%i:%s' ) as kohsamui_create_date, a.kohsamui_create_by "                                                                      . "FROM tbl_kohsamui as a "                                    . " ".$cond;		$query		= $this->db->query($query)->result_array();		return $query;	}         		function getListKohsamuiAlias(){		 $query		= "SELECT kohsamui_id, kohsamui_title, kohsamui_image, kohsamui_desc,						DATE_FORMAT( kohsamui_create_date, '%d %M %Y' ) as kohsamui_create_date,						c.web_alias, c.web_ori						FROM tbl_kohsamui a						INNER JOIN tbl_alias c ON a.kohsamui_id = c.key_id 						WHERE a.kohsamui_active_status = 1 AND c.alias_category = 'kohsamui' AND c.alias_active_status = 1						ORDER BY kohsamui_id DESC";		$query		= $this->db->query($query)->result_array();		return $query;	}		function getKohsamui($id = '')	{		$where = '';		if($id != ''){			$where = "WHERE kohsamui_id = ".$id;		}		 $sql	= "SELECT a.kohsamui_id,a.kohsamui_title,a.kohsamui_image, a.kohsamui_active_status,a.kohsamui_desc, "                                    . "DATE_FORMAT( a.kohsamui_create_date, '%d-%m-%Y %H:%i:%s' ) as kohsamui_create_date, a.kohsamui_create_by "                                                                      . "FROM tbl_kohsamui as a "                                    . " $where "                                    . " ORDER BY kohsamui_id ASC";			$query	= $this->db->query($sql);		$rs		= $query->result_array(); 		return $rs;		}	function checkKohsamui($kohsamui_title){		$sql	= "SELECT kohsamui_title FROM tbl_kohsamui WHERE kohsamui_title = '".$kohsamui_title."'";		$query	= $this->db->query($sql);		$rs		= $query->result_array(); 		return $rs;	}	function activeKohsamui($id)	{		$sql	= "UPDATE tbl_kohsamui SET kohsamui_active_status = abs(kohsamui_active_status-1),  				   kohsamui_update_date = now(), 				   kohsamui_update_by = ".$_SESSION['admin_data']['user_id']."				   WHERE kohsamui_id = ".$id;			$query	= $this->db->query($sql);				return $query;	}	function deleteKohsamui($id = '')	{		$sql	= "DELETE FROM tbl_kohsamui WHERE kohsamui_id = ".$id;			$query	= $this->db->query($sql);				$str = $this->db->last_query();				return $str;	}        function insertKohsamui($kohsamui_title, $kohsamui_imageurl,$kohsamui_desc)	{		$sql	= "INSERT INTO tbl_kohsamui SET                             kohsamui_title='".$kohsamui_title."',                            kohsamui_image='".$kohsamui_imageurl."',                            kohsamui_desc='".$kohsamui_desc."',                            kohsamui_create_by = ".$_SESSION['admin_data']['user_id'].", kohsamui_create_date = now()";					$query  = $this->db->query($sql);		$last_id  = $this->db->insert_id();		return $last_id;	}	function updateKohsamui($id,$kohsamui_title, $kohsamui_imageurl,$kohsamui_desc)	{		 $sql	= "UPDATE tbl_kohsamui SET                             kohsamui_title='".$kohsamui_title."',                            kohsamui_image='".$kohsamui_imageurl."',                            kohsamui_desc='".$kohsamui_desc."',                            kohsamui_update_by = ".$_SESSION['admin_data']['user_id'].",                             kohsamui_update_date=now() WHERE kohsamui_id = ".$id;			$query	= $this->db->query($sql);		return $query;	}         function updateOrderKohsamui($id,$order)	{		$sql	= "UPDATE tbl_kohsamui SET 				   kohsamui_order= ".$order.",				   kohsamui_update_by = ".$_SESSION['admin_data']['user_id'].", kohsamui_update_date=now() WHERE kohsamui_id = ".$id;			$query	= $this->db->query($sql);				return $query;	}         function getPageView(){         $data = array();         $sql="select * from tbl_page_view order by page_type asc";                  $hasil = $this->db->query($sql);                        if($hasil->num_rows() > 0){				$data = $hasil->result();			}                        else{                            $data = '';                        }			$hasil->free_result();                        $this->db->close();			return $data;              }          function GenerateKohsamui($cond = null){            $query		 = "SELECT  a.kohsamui_id,  a.kohsamui_title,  a.kohsamui_image, a.kohsamui_desc,a.kohsamui_order, "                                    . "a.kohsamui_active_status "                                                                    . "FROM tbl_kohsamui as a "                                    . " ".$cond;                    $query = $this->db->query($query)->result_array();                    $data  = array();                    foreach ($query as $row) {                    $data[] = array(                    'kohsamui_id' => $row['kohsamui_id'],                    'kohsamui_title' => $row['kohsamui_title'],                    'kohsamui_name' => $row['kohsamui_name'],                    'kohsamui_image' => $row['kohsamui_image'],                    'kohsamui_desc' => $row['kohsamui_desc'],                    'kohsamui_order' => $row['kohsamui_order'],                    'kohsamui_urla' => $row['kohsamui_urla'],                    'kohsamui_urlb' => $row['kohsamui_urlb']                );            }            //$query->free_result();            $this->db->close();            return $data;	} function getMenu($kohsamui_id){		 $query	 = "SELECT a.kohsamui_menu_id, a.kohsamui_menu_title,a.kohsamui_menu_desc,"                                    . " a.kohsamui_menu_active_status,"                                    . "a.kohsamui_menu_order "                                    . "FROM tbl_kohsamui_menu as a "                                    . " inner join tbl_kohsamui as b on a.kohsamui_id = b.kohsamui_id "                                    . " where a.kohsamui_menu_active_status =1 and a.kohsamui_id =".$kohsamui_id." order by a.kohsamui_menu_order ASC";                                    		$query		= $this->db->query($query)->result_array();		return $query;	}     }