<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class Model_ocean extends CI_Model {    function __construct()    {        parent::__construct();    }        function getModule($module_name){            $data = array();            $sql="select module_id , module_group_id from tbl_module where module_path='".$module_name."' ";         		 $hasil = $this->db->query($sql);                        if($hasil->num_rows() > 0){				$data = $hasil->result();			}                        else{                            $data = '';                        }			$hasil->free_result();                        $this->db->close();			return $data;                 }         function getListOcean($cond = null){		  $query	    = "SELECT  a.ocean_id,  a.ocean_title, a.ocean_image, a.ocean_desc,a.ocean_order,"                                    . " a.ocean_active_status,"                                    . "DATE_FORMAT( a.ocean_create_date, '%d-%m-%Y %H:%i:%s' ) as ocean_create_date, a.ocean_create_by "                                                                      . "FROM tbl_ocean as a "                                    . " ".$cond;		$query		= $this->db->query($query)->result_array();		return $query;	}         		function getListOceanAlias(){		 $query		= "SELECT ocean_id, ocean_title, ocean_image, ocean_desc,						DATE_FORMAT( ocean_create_date, '%d %M %Y' ) as ocean_create_date,						c.web_alias, c.web_ori						FROM tbl_ocean a						INNER JOIN tbl_alias c ON a.ocean_id = c.key_id 						WHERE a.ocean_active_status = 1 AND c.alias_category = 'ocean' AND c.alias_active_status = 1						ORDER BY ocean_id DESC";		$query		= $this->db->query($query)->result_array();		return $query;	}		function getOcean($id = '')	{		$where = '';		if($id != ''){			$where = "WHERE ocean_id = ".$id;		}		 $sql	= "SELECT a.ocean_id,a.ocean_title,a.ocean_name,a.ocean_image, a.ocean_active_status,a.ocean_desc,a.ocean_urla,a.ocean_urlb, "                                    . "DATE_FORMAT( a.ocean_create_date, '%d-%m-%Y %H:%i:%s' ) as ocean_create_date, a.ocean_create_by "                                                                      . "FROM tbl_ocean as a "                                    . " $where "                                    . " ORDER BY ocean_id ASC";			$query	= $this->db->query($sql);		$rs		= $query->result_array(); 		return $rs;		}	function checkOcean($ocean_title){		$sql	= "SELECT ocean_title FROM tbl_ocean WHERE ocean_title = '".$ocean_title."'";		$query	= $this->db->query($sql);		$rs		= $query->result_array(); 		return $rs;	}	function activeOcean($id)	{		$sql	= "UPDATE tbl_ocean SET ocean_active_status = abs(ocean_active_status-1),  				   ocean_update_date = now(), 				   ocean_update_by = ".$_SESSION['admin_data']['user_id']."				   WHERE ocean_id = ".$id;			$query	= $this->db->query($sql);				return $query;	}	function deleteOcean($id = '')	{		$sql	= "DELETE FROM tbl_ocean WHERE ocean_id = ".$id;			$query	= $this->db->query($sql);				$str = $this->db->last_query();				return $str;	}        function insertOcean($ocean_title,$ocean_name, $ocean_imageurl,$ocean_desc,$ocean_urla,$ocean_urlb)	{		$sql	= "INSERT INTO tbl_ocean SET                             ocean_title='".$ocean_title."',                            ocean_name='".$ocean_name."',                            ocean_image='".$ocean_imageurl."',                            ocean_desc='".$ocean_desc."',                            ocean_urla='".$ocean_urla."',                            ocean_urlb='".$ocean_urlb."',                            ocean_create_by = ".$_SESSION['admin_data']['user_id'].", ocean_create_date = now()";					$query  = $this->db->query($sql);		$last_id  = $this->db->insert_id();		return $last_id;	}	function updateOcean($id,$ocean_title,$ocean_name, $ocean_imageurl,$ocean_desc,$ocean_urla,$ocean_urlb)	{		 $sql	= "UPDATE tbl_ocean SET                             ocean_title='".$ocean_title."',                            ocean_name='".$ocean_name."',                            ocean_image='".$ocean_imageurl."',                            ocean_desc='".$ocean_desc."',                            ocean_urla='".$ocean_urla."',                            ocean_urlb='".$ocean_urlb."',                            ocean_update_by = ".$_SESSION['admin_data']['user_id'].",                             ocean_update_date=now() WHERE ocean_id = ".$id;			$query	= $this->db->query($sql);		return $query;	}         function updateOrderOcean($id,$order)	{		$sql	= "UPDATE tbl_ocean SET 				   ocean_order= ".$order.",				   ocean_update_by = ".$_SESSION['admin_data']['user_id'].", ocean_update_date=now() WHERE ocean_id = ".$id;			$query	= $this->db->query($sql);				return $query;	}         function getPageView(){         $data = array();         $sql="select * from tbl_page_view order by page_type asc";                  $hasil = $this->db->query($sql);                        if($hasil->num_rows() > 0){				$data = $hasil->result();			}                        else{                            $data = '';                        }			$hasil->free_result();                        $this->db->close();			return $data;              }          function GenerateOcean($cond = null){            $query		 = "SELECT  a.ocean_id,  a.ocean_title, a.ocean_name, a.ocean_image, a.ocean_desc,a.ocean_order, a.ocean_urla,a.ocean_urlb,"                                    . "a.ocean_active_status "                                                                    . "FROM tbl_ocean as a "                                    . " ".$cond;                    $query = $this->db->query($query)->result_array();                    $data  = array();                    foreach ($query as $row) {                    $data[] = array(                    'ocean_id' => $row['ocean_id'],                    'ocean_title' => $row['ocean_title'],                    'ocean_name' => $row['ocean_name'],                    'ocean_image' => $row['ocean_image'],                    'ocean_desc' => $row['ocean_desc'],                    'ocean_order' => $row['ocean_order'],                    'ocean_urla' => $row['ocean_urla'],                    'ocean_urlb' => $row['ocean_urlb']                );            }            //$query->free_result();            $this->db->close();            return $data;	} function getMenu($ocean_id){		 $query	 = "SELECT a.ocean_menu_id, a.ocean_menu_title,a.ocean_menu_desc,"                                    . " a.ocean_menu_active_status,"                                    . "a.ocean_menu_order "                                    . "FROM tbl_ocean_menu as a "                                    . " inner join tbl_ocean as b on a.ocean_id = b.ocean_id "                                    . " where a.ocean_menu_active_status =1 and a.ocean_id =".$ocean_id." order by a.ocean_menu_order ASC";                                    		$query		= $this->db->query($query)->result_array();		return $query;	}     }