<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class Model_privacy extends CI_Model {    function __construct()    {        parent::__construct();    }        function getModule($module_name){            $data = array();            $sql="select module_id , module_group_id from tbl_module where module_path='".$module_name."' ";         		 $hasil = $this->db->query($sql);                        if($hasil->num_rows() > 0){				$data = $hasil->result();			}                        else{                            $data = '';                        }			$hasil->free_result();                        $this->db->close();			return $data;                 }         function getListPrivacy($cond = null){		  $query	    = "SELECT  a.privacy_id,  a.privacy_title, a.privacy_image, a.privacy_desc,a.privacy_order,"                                    . " a.privacy_active_status,"                                    . "DATE_FORMAT( a.privacy_create_date, '%d-%m-%Y %H:%i:%s' ) as privacy_create_date, a.privacy_create_by "                                                                      . "FROM tbl_privacy as a "                                    . " ".$cond;		$query		= $this->db->query($query)->result_array();		return $query;	}         		function getListPrivacyAlias(){		 $query		= "SELECT privacy_id, privacy_title, privacy_image, privacy_desc,						DATE_FORMAT( privacy_create_date, '%d %M %Y' ) as privacy_create_date,						c.web_alias, c.web_ori						FROM tbl_privacy a						INNER JOIN tbl_alias c ON a.privacy_id = c.key_id 						WHERE a.privacy_active_status = 1 AND c.alias_category = 'privacy' AND c.alias_active_status = 1						ORDER BY privacy_id DESC";		$query		= $this->db->query($query)->result_array();		return $query;	}		function getPrivacy($id = '')	{		$where = '';		if($id != ''){			$where = "WHERE privacy_id = ".$id;		}		 $sql	= "SELECT a.privacy_id,a.privacy_title,a.privacy_image, a.privacy_active_status,a.privacy_desc, "                                    . "DATE_FORMAT( a.privacy_create_date, '%d-%m-%Y %H:%i:%s' ) as privacy_create_date, a.privacy_create_by "                                                                      . "FROM tbl_privacy as a "                                    . " $where "                                    . " ORDER BY privacy_id ASC";			$query	= $this->db->query($sql);		$rs		= $query->result_array(); 		return $rs;		}	function checkPrivacy($privacy_title){		$sql	= "SELECT privacy_title FROM tbl_privacy WHERE privacy_title = '".$privacy_title."'";		$query	= $this->db->query($sql);		$rs		= $query->result_array(); 		return $rs;	}	function activePrivacy($id)	{		$sql	= "UPDATE tbl_privacy SET privacy_active_status = abs(privacy_active_status-1),  				   privacy_update_date = now(), 				   privacy_update_by = ".$_SESSION['admin_data']['user_id']."				   WHERE privacy_id = ".$id;			$query	= $this->db->query($sql);				return $query;	}	function deletePrivacy($id = '')	{		$sql	= "DELETE FROM tbl_privacy WHERE privacy_id = ".$id;			$query	= $this->db->query($sql);				$str = $this->db->last_query();				return $str;	}        function insertPrivacy($privacy_title, $privacy_imageurl,$privacy_desc)	{		$sql	= "INSERT INTO tbl_privacy SET                             privacy_title='".$privacy_title."',                            privacy_image='".$privacy_imageurl."',                            privacy_desc='".$privacy_desc."',                            privacy_create_by = ".$_SESSION['admin_data']['user_id'].", privacy_create_date = now()";					$query  = $this->db->query($sql);		$last_id  = $this->db->insert_id();		return $last_id;	}	function updatePrivacy($id,$privacy_title, $privacy_imageurl,$privacy_desc)	{		 $sql	= "UPDATE tbl_privacy SET                             privacy_title='".$privacy_title."',                            privacy_image='".$privacy_imageurl."',                            privacy_desc='".$privacy_desc."',                            privacy_update_by = ".$_SESSION['admin_data']['user_id'].",                             privacy_update_date=now() WHERE privacy_id = ".$id;			$query	= $this->db->query($sql);		return $query;	}         function updateOrderPrivacy($id,$order)	{		$sql	= "UPDATE tbl_privacy SET 				   privacy_order= ".$order.",				   privacy_update_by = ".$_SESSION['admin_data']['user_id'].", privacy_update_date=now() WHERE privacy_id = ".$id;			$query	= $this->db->query($sql);				return $query;	}         function getPageView(){         $data = array();         $sql="select * from tbl_page_view order by page_type asc";                  $hasil = $this->db->query($sql);                        if($hasil->num_rows() > 0){				$data = $hasil->result();			}                        else{                            $data = '';                        }			$hasil->free_result();                        $this->db->close();			return $data;              }          function GeneratePrivacy($cond = null){            $query		 = "SELECT  a.privacy_id,  a.privacy_title,  a.privacy_image, a.privacy_desc,a.privacy_order, "                                    . "a.privacy_active_status "                                                                    . "FROM tbl_privacy as a "                                    . " ".$cond;                    $query = $this->db->query($query)->result_array();                    $data  = array();                    foreach ($query as $row) {                    $data[] = array(                    'privacy_id' => $row['privacy_id'],                    'privacy_title' => $row['privacy_title'],                    'privacy_name' => $row['privacy_name'],                    'privacy_image' => $row['privacy_image'],                    'privacy_desc' => $row['privacy_desc'],                    'privacy_order' => $row['privacy_order'],                    'privacy_urla' => $row['privacy_urla'],                    'privacy_urlb' => $row['privacy_urlb']                );            }            //$query->free_result();            $this->db->close();            return $data;	} function getMenu($privacy_id){		 $query	 = "SELECT a.privacy_menu_id, a.privacy_menu_title,a.privacy_menu_desc,"                                    . " a.privacy_menu_active_status,"                                    . "a.privacy_menu_order "                                    . "FROM tbl_privacy_menu as a "                                    . " inner join tbl_privacy as b on a.privacy_id = b.privacy_id "                                    . " where a.privacy_menu_active_status =1 and a.privacy_id =".$privacy_id." order by a.privacy_menu_order ASC";                                    		$query		= $this->db->query($query)->result_array();		return $query;	}     }