<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class Model_privates extends CI_Model {    function __construct()    {        parent::__construct();    }        function getModule($module_name){            $data = array();            $sql="select module_id , module_group_id from tbl_module where module_path='".$module_name."' ";         		 $hasil = $this->db->query($sql);                        if($hasil->num_rows() > 0){				$data = $hasil->result();			}                        else{                            $data = '';                        }			$hasil->free_result();                        $this->db->close();			return $data;                 }         function getListPrivates($cond = null){		  $query	    = "SELECT  a.privates_id,  a.privates_title, a.privates_image, a.privates_desc,a.privates_order,"                                    . " a.privates_active_status,"                                    . "DATE_FORMAT( a.privates_create_date, '%d-%m-%Y %H:%i:%s' ) as privates_create_date, a.privates_create_by "                                                                      . "FROM tbl_privates as a "                                    . " ".$cond;		$query		= $this->db->query($query)->result_array();		return $query;	}         		function getListPrivatesAlias(){		 $query		= "SELECT privates_id, privates_title, privates_image, privates_desc,						DATE_FORMAT( privates_create_date, '%d %M %Y' ) as privates_create_date,						c.web_alias, c.web_ori						FROM tbl_privates a						INNER JOIN tbl_alias c ON a.privates_id = c.key_id 						WHERE a.privates_active_status = 1 AND c.alias_category = 'privates' AND c.alias_active_status = 1						ORDER BY privates_id DESC";		$query		= $this->db->query($query)->result_array();		return $query;	}		function getPrivates($id = '')	{		$where = '';		if($id != ''){			$where = "WHERE privates_id = ".$id;		}		 $sql	= "SELECT a.privates_id,a.privates_title,a.privates_name,a.privates_image, a.privates_active_status,a.privates_desc,a.privates_urla,a.privates_urlb, "                                    . "DATE_FORMAT( a.privates_create_date, '%d-%m-%Y %H:%i:%s' ) as privates_create_date, a.privates_create_by "                                                                      . "FROM tbl_privates as a "                                    . " $where "                                    . " ORDER BY privates_id ASC";			$query	= $this->db->query($sql);		$rs		= $query->result_array(); 		return $rs;		}	function checkPrivates($privates_title){		$sql	= "SELECT privates_title FROM tbl_privates WHERE privates_title = '".$privates_title."'";		$query	= $this->db->query($sql);		$rs		= $query->result_array(); 		return $rs;	}	function activePrivates($id)	{		$sql	= "UPDATE tbl_privates SET privates_active_status = abs(privates_active_status-1),  				   privates_update_date = now(), 				   privates_update_by = ".$_SESSION['admin_data']['user_id']."				   WHERE privates_id = ".$id;			$query	= $this->db->query($sql);				return $query;	}	function deletePrivates($id = '')	{		$sql	= "DELETE FROM tbl_privates WHERE privates_id = ".$id;			$query	= $this->db->query($sql);				$str = $this->db->last_query();				return $str;	}        function insertPrivates($privates_title, $privates_imageurl,$privates_desc,$privates_urla,$privates_urlb)	{		$sql	= "INSERT INTO tbl_privates SET                             privates_title='".$privates_title."',                            privates_image='".$privates_imageurl."',                            privates_desc='".$privates_desc."',                            privates_urla='".$privates_urla."',                            privates_urlb='".$privates_urlb."',                            privates_create_by = ".$_SESSION['admin_data']['user_id'].", privates_create_date = now()";					$query  = $this->db->query($sql);		$last_id  = $this->db->insert_id();		return $last_id;	}	function updatePrivates($id,$privates_title, $privates_imageurl,$privates_desc,$privates_urla,$privates_urlb)	{		 $sql	= "UPDATE tbl_privates SET                             privates_title='".$privates_title."',                            privates_image='".$privates_imageurl."',                            privates_desc='".$privates_desc."',                            privates_urla='".$privates_urla."',                            privates_urlb='".$privates_urlb."',                            privates_update_by = ".$_SESSION['admin_data']['user_id'].",                             privates_update_date=now() WHERE privates_id = ".$id;			$query	= $this->db->query($sql);		return $query;	}         function updateOrderPrivates($id,$order)	{		$sql	= "UPDATE tbl_privates SET 				   privates_order= ".$order.",				   privates_update_by = ".$_SESSION['admin_data']['user_id'].", privates_update_date=now() WHERE privates_id = ".$id;			$query	= $this->db->query($sql);				return $query;	}         function getPageView(){         $data = array();         $sql="select * from tbl_page_view order by page_type asc";                  $hasil = $this->db->query($sql);                        if($hasil->num_rows() > 0){				$data = $hasil->result();			}                        else{                            $data = '';                        }			$hasil->free_result();                        $this->db->close();			return $data;              }          function GeneratePrivates($cond = null){            $query		 = "SELECT  a.privates_id,  a.privates_title, a.privates_name, a.privates_image, a.privates_desc,a.privates_order, a.privates_urla,a.privates_urlb,"                                    . "a.privates_active_status "                                                                    . "FROM tbl_privates as a "                                    . " ".$cond;                    $query = $this->db->query($query)->result_array();                    $data  = array();                    foreach ($query as $row) {                    $data[] = array(                    'privates_id' => $row['privates_id'],                    'privates_title' => $row['privates_title'],                    'privates_image' => $row['privates_image'],                    'privates_desc' => $row['privates_desc'],                    'privates_order' => $row['privates_order'],                    'privates_urla' => $row['privates_urla'],                    'privates_urlb' => $row['privates_urlb']                );            }            //$query->free_result();            $this->db->close();            return $data;	} function getMenu($privates_id){		 $query	 = "SELECT a.privates_menu_id, a.privates_menu_title,a.privates_menu_desc,"                                    . " a.privates_menu_active_status,"                                    . "a.privates_menu_order "                                    . "FROM tbl_privates_menu as a "                                    . " inner join tbl_privates as b on a.privates_id = b.privates_id "                                    . " where a.privates_menu_active_status =1 and a.privates_id =".$privates_id." order by a.privates_menu_order ASC";                                    		$query		= $this->db->query($query)->result_array();		return $query;	}     }