<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class Model_programme extends CI_Model {    function __construct()    {        parent::__construct();    }        function getModule($module_name){            $data = array();            $sql="select module_id , module_group_id from tbl_module where module_path='".$module_name."' ";         		 $hasil = $this->db->query($sql);                        if($hasil->num_rows() > 0){				$data = $hasil->result();			}                        else{                            $data = '';                        }			$hasil->free_result();                        $this->db->close();			return $data;                 }         function getListProgramme($cond = null){		  $query	    = "SELECT  a.programme_id,  a.programme_title, a.programme_image, a.programme_desc, a.programme_title2, a.programme_desc2,a.programme_urla,a.programme_order, "                                    . " a.programme_image, a.programme_active_status,"                                    . "DATE_FORMAT( a.programme_create_date, '%d-%m-%Y %H:%i:%s' ) as programme_create_date, a.programme_create_by "                                                                      . "FROM tbl_programme as a "                                    . " ".$cond;		$query		= $this->db->query($query)->result_array();		return $query;	}         		function getListProgrammeAlias(){		 $query		= "SELECT programme_id, programme_title, programme_image, programme_desc,						DATE_FORMAT( programme_create_date, '%d %M %Y' ) as programme_create_date,						c.web_alias, c.web_ori						FROM tbl_programme a						INNER JOIN tbl_alias c ON a.programme_id = c.key_id 						WHERE a.programme_active_status = 1 AND c.alias_category = 'programme' AND c.alias_active_status = 1						ORDER BY programme_id DESC";		$query		= $this->db->query($query)->result_array();		return $query;	}		function getProgramme($id = '')	{		$where = '';		if($id != ''){			$where = "WHERE programme_id = ".$id;		}		 $sql	= "SELECT  a.programme_id,  a.programme_title, a.programme_image, a.programme_desc, a.programme_title2, a.programme_desc2,a.programme_urla,a.programme_order, "                                    . "DATE_FORMAT( a.programme_create_date, '%d-%m-%Y %H:%i:%s' ) as programme_create_date, a.programme_create_by "                                                                      . "FROM tbl_programme as a "                                    . " $where "                                    . " ORDER BY programme_id ASC";			$query	= $this->db->query($sql);		$rs		= $query->result_array(); 		return $rs;		}	function checkProgramme($programme_title){		$sql	= "SELECT programme_title FROM tbl_programme WHERE programme_title = '".$programme_title."'";		$query	= $this->db->query($sql);		$rs		= $query->result_array(); 		return $rs;	}	function activeProgramme($id)	{		$sql	= "UPDATE tbl_programme SET programme_active_status = abs(programme_active_status-1),  				   programme_update_date = now(), 				   programme_update_by = ".$_SESSION['admin_data']['user_id']."				   WHERE programme_id = ".$id;			$query	= $this->db->query($sql);				return $query;	}	function deleteProgramme($id = '')	{		$sql	= "DELETE FROM tbl_programme WHERE programme_id = ".$id;			$query	= $this->db->query($sql);				$str = $this->db->last_query();				return $str;	}        function insertProgramme($programme_title,$programme_desc,$programme_title2,$programme_desc2,$programme_imageurl,$programme_urla)	{		$sql	= "INSERT INTO tbl_programme SET                             programme_title='".$programme_title."',                            programme_desc='".$programme_desc."',                            programme_title2='".$programme_title2."',                            programme_desc2='".$programme_desc2."',                            programme_image='".$programme_imageurl."',                            programme_urla='".$programme_urla."',                            programme_create_by = ".$_SESSION['admin_data']['user_id'].", programme_create_date = now()";					$query  = $this->db->query($sql);		$last_id  = $this->db->insert_id();		return $last_id;	}	function updateProgramme($id,$programme_title,$programme_desc,$programme_title2,$programme_desc2,$programme_imageurl,$programme_urla)	{		 $sql	= "UPDATE tbl_programme SET                             programme_title='".$programme_title."',                            programme_desc='".$programme_desc."',                            programme_title2='".$programme_title2."',                            programme_desc2='".$programme_desc2."',                            programme_image='".$programme_imageurl."',                            programme_urla='".$programme_urla."',                            programme_update_by = ".$_SESSION['admin_data']['user_id'].",                             programme_update_date=now() WHERE programme_id = ".$id;			$query	= $this->db->query($sql);		return $query;	}         function updateOrderProgramme($id,$order)	{		$sql	= "UPDATE tbl_programme SET 				   programme_order= ".$order.",				   programme_update_by = ".$_SESSION['admin_data']['user_id'].", programme_update_date=now() WHERE programme_id = ".$id;			$query	= $this->db->query($sql);				return $query;	}         function getPageView(){         $data = array();         $sql="select * from tbl_page_view order by page_type asc";                  $hasil = $this->db->query($sql);                        if($hasil->num_rows() > 0){				$data = $hasil->result();			}                        else{                            $data = '';                        }			$hasil->free_result();                        $this->db->close();			return $data;              }          function GenerateProgramme($cond = null){            $query		 = "SELECT  a.programme_id,  a.programme_title, a.programme_image, a.programme_desc, a.programme_title2, a.programme_desc2,a.programme_urla,a.programme_order, "                                    . "DATE_FORMAT( a.programme_create_date, '%d-%m-%Y %H:%i:%s' ) as programme_create_date, a.programme_create_by "                                                                      . "FROM tbl_programme as a "                                    . " ".$cond;                    $query = $this->db->query($query)->result_array();                    $data  = array();                    foreach ($query as $row) {                    $data[] = array(                    'programme_id' => $row['programme_id'],                    'programme_title' => $row['programme_title'],                    'programme_image' => $row['programme_image'],                    'programme_desc' => $row['programme_desc'],                    'programme_title2' => $row['programme_title2'],                    'programme_desc2' => $row['programme_desc2'],                    'programme_order' => $row['programme_order'],                    'programme_urla' => $row['programme_urla'],                    'menu' => $this->getMenu($row['programme_id'])                );            }            //$query->free_result();            $this->db->close();            return $data;	} function getMenu($programme_id){		 $query	 = "SELECT a.programme_menu_id, a.programme_menu_title,a.programme_menu_desc,a.programme_menu_urla,a.programme_menu_urlb, "                                    . " a.programme_menu_active_status,"                                    . "a.programme_menu_order "                                    . "FROM tbl_programme_menu as a "                                    . " inner join tbl_programme as b on a.programme_id = b.programme_id "                                    . " where a.programme_menu_active_status =1 and a.programme_id =".$programme_id." order by a.programme_menu_order ASC";                                    		$query		= $this->db->query($query)->result_array();		return $query;	}     }