<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class Model_programme_menu extends CI_Model {    function __construct()    {        parent::__construct();    }		function getMenu($programme_id,$cond = null){		 $query	 = "SELECT a.programme_menu_id, a.programme_menu_title,a.programme_menu_desc, a.programme_menu_urla,a.programme_menu_urlb, "                                    . " a.programme_menu_active_status,"                                    . "a.programme_menu_order,"                                    . "DATE_FORMAT( a.programme_menu_create_date, '%d-%m-%Y %H:%i:%s' ) as programme_menu_create_date, a.programme_menu_create_by "                                    . "FROM tbl_programme_menu as a "                                    . " inner join tbl_programme as b on a.programme_id = b.programme_id "                                    . " where a.programme_id=".$programme_id." "                                    . " ".$cond;		$query		= $this->db->query($query)->result_array();		return $query;	}	    function getTitle($programme_menu_id){            $data = array();             $hasil =   $this->db->query("select material_title as title from tbl_programme_menu where programme_menu_id = $programme_menu_id ");				$data = $hasil->row_array();                                               return $data['title'];         }		function getMenuBy($id = '')	{		$where = '';		if($id != ''){			$where = "WHERE programme_menu_id = ".$id;		}		$sql	= "SELECT a.programme_menu_id, a.programme_menu_title,a.programme_menu_desc,a.programme_menu_urla,a.programme_menu_urlb, "                                    . "  a.programme_menu_active_status,"                                    . "a.programme_menu_order,"                                    . "DATE_FORMAT( a.programme_menu_create_date, '%d-%m-%Y %H:%i:%s' ) as programme_menu_create_date, a.programme_menu_create_by, "                                    . " b.programme_id "                                    . "FROM tbl_programme_menu as a "                                    . " inner join tbl_programme as b on a.programme_id = b.programme_id "                                    . " $where "                                    . " ORDER BY programme_menu_id ASC";			$query	= $this->db->query($sql);		$rs		= $query->result_array(); 				return $rs;		}		function activeMenu($id)	{		 $sql	= "UPDATE tbl_programme_menu SET programme_menu_active_status = abs(programme_menu_active_status-1),  				   programme_menu_update_date = now(), 				   programme_menu_update_by = ".$_SESSION['admin_data']['user_id']."				   WHERE programme_menu_id = ".$id;			$query	= $this->db->query($sql);				return $query;	}		function deleteMenu($id = '')	{		$sql	= "DELETE FROM tbl_programme_menu WHERE programme_id = ".$id;			$query	= $this->db->query($sql);				$str = $this->db->last_query();				return $str;	}	function deleteMenuby($id = '')	{		$sql	= "DELETE FROM tbl_programme_menu WHERE programme_menu_id = ".$id;			$query	= $this->db->query($sql);				$str = $this->db->last_query();				return $str;	}	function checkMenu($programme_menu_title, $programme_id){		$sql	= "SELECT programme_menu_title FROM tbl_programme_menu WHERE programme_menu_title = '".$programme_menu_title."' and programme_id = '".$programme_id."'";		$query	= $this->db->query($sql);		$rs		= $query->result_array(); 		return $rs;	}	            function insertMenu($data){                foreach ($data as $value) {                  $this->db->insert('tbl_programme_menu', $value);                   }                $this->db->close();	}       	function editMenu($programme_menu_title,$programme_menu_desc,$programme_menu_urla,$programme_menu_urlb,$programme_menu_order,$programme_menu_status,$programme_menu_id){          $today= date("Y-m-d H:i:s");          $data = array(                        'programme_menu_title'=>$programme_menu_title,                        'programme_menu_desc'=>$programme_menu_desc,                        'programme_menu_urla'=>$programme_menu_urla,                        'programme_menu_urlb'=>$programme_menu_urlb,                        'programme_menu_order'=>$programme_menu_order,                        'programme_menu_active_status'=>$programme_menu_status,                        'programme_menu_update_by'=> $_SESSION['admin_data']['user_id'],                         'programme_menu_update_date'=> $today                        );            $this->db->where('programme_menu_id', $programme_menu_id);            $this->db->update('tbl_programme_menu', $data);            $this->db->close();	        }     function updateOrderMenu($id,$order)	{		$sql	= "UPDATE tbl_programme_menu SET 				   programme_menu_order= ".$order.",				   programme_menu_update_by = ".$_SESSION['admin_data']['user_id'].", programme_menu_update_date=now() WHERE programme_menu_id = ".$id;			$query	= $this->db->query($sql);		return $query;	}      function generateProgramme(){		$query	= "SELECT a.programme_menu_id, a.programme_menu_title,a.programme_menu_desc, a.programme_menu_urla,a.programme_menu_urlb, "                                    . "  a.programme_menu_active_status,"                                    . "a.programme_menu_order,"                                    . "DATE_FORMAT( a.programme_menu_create_date, '%d-%m-%Y %H:%i:%s' ) as programme_menu_create_date, a.programme_menu_create_by, "                                    . " b.programme_id "                                    . "FROM tbl_programme_menu as a "                                    . " inner join tbl_programme as b on a.programme_id = b.programme_id "                                    . " where b.programme_active_status=1 and a.programme_menu_active_status=1 "                                    . " ORDER BY programme_menu_id ASC";		$query		= $this->db->query($query)->result_array();				return $query;	}        }