<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class Model_promo extends CI_Model {    function __construct()    {        parent::__construct();    }        function getModule($module_name){            $data = array();            $sql="select module_id , module_group_id from tbl_module where module_path='".$module_name."' ";         		 $hasil = $this->db->query($sql);                        if($hasil->num_rows() > 0){				$data = $hasil->result();			}                        else{                            $data = '';                        }			$hasil->free_result();                        $this->db->close();			return $data;                 }         function getListPromo($cond = null){		  $query	    = "SELECT  a.promo_id,  a.promo_title,  a.promo_desc,"                                    . "  a.promo_active_status,"                                    . "DATE_FORMAT( a.promo_create_date, '%d-%m-%Y %H:%i:%s' ) as promo_create_date, a.promo_create_by "                                                                      . "FROM tbl_promo as a "                                    . " ".$cond;		$query		= $this->db->query($query)->result_array();		return $query;	}         		function getListPromoAlias(){		 $query		= "SELECT promo_id, promo_title, promo_image, promo_desc,						DATE_FORMAT( promo_create_date, '%d %M %Y' ) as promo_create_date,						c.web_alias, c.web_ori						FROM tbl_promo a						INNER JOIN tbl_alias c ON a.promo_id = c.key_id 						WHERE a.promo_active_status = 1 AND c.alias_category = 'promo' AND c.alias_active_status = 1						ORDER BY promo_id DESC";		$query		= $this->db->query($query)->result_array();		return $query;	}		function getPromo($id = '')	{		$where = '';		if($id != ''){			$where = "WHERE promo_id = ".$id;		}		 $sql	= "SELECT a.promo_id,a.promo_title, a.promo_active_status,a.promo_desc, "                                    . "DATE_FORMAT( a.promo_create_date, '%d-%m-%Y %H:%i:%s' ) as promo_create_date, a.promo_create_by "                                                                      . "FROM tbl_promo as a "                                    . " $where "                                    . " ORDER BY promo_id ASC";			$query	= $this->db->query($sql);		$rs		= $query->result_array(); 		return $rs;		}	function checkPromo($promo_title){		$sql	= "SELECT promo_title FROM tbl_promo WHERE promo_title = '".$promo_title."'";		$query	= $this->db->query($sql);		$rs		= $query->result_array(); 		return $rs;	}	function activePromo($id)	{		$sql	= "UPDATE tbl_promo SET promo_active_status = abs(promo_active_status-1),  				   promo_update_date = now(), 				   promo_update_by = ".$_SESSION['admin_data']['user_id']."				   WHERE promo_id = ".$id;			$query	= $this->db->query($sql);				return $query;	}	function deletePromo($id = '')	{		$sql	= "DELETE FROM tbl_promo WHERE promo_id = ".$id;			$query	= $this->db->query($sql);				$str = $this->db->last_query();				return $str;	}        function insertPromo($promo_title,$promo_desc)	{		$sql	= "INSERT INTO tbl_promo SET                             promo_title='".$promo_title."',                            promo_desc='".$promo_desc."',                            promo_create_by = ".$_SESSION['admin_data']['user_id'].", promo_create_date = now()";					$query  = $this->db->query($sql);		$last_id  = $this->db->insert_id();		return $last_id;	}	function updatePromo($id,$promo_title,$promo_desc)	{		 $sql	= "UPDATE tbl_promo SET                             promo_title='".$promo_title."',                             promo_desc='".$promo_desc."',                            promo_update_by = ".$_SESSION['admin_data']['user_id'].",                             promo_update_date=now() WHERE promo_id = ".$id;			$query	= $this->db->query($sql);		return $query;	}         function updateOrderPromo($id,$order)	{		$sql	= "UPDATE tbl_promo SET 				   promo_order= ".$order.",				   promo_update_by = ".$_SESSION['admin_data']['user_id'].", promo_update_date=now() WHERE promo_id = ".$id;			$query	= $this->db->query($sql);				return $query;	}         function getPageView(){         $data = array();         $sql="select * from tbl_page_view order by page_type asc";                  $hasil = $this->db->query($sql);                        if($hasil->num_rows() > 0){				$data = $hasil->result();			}                        else{                            $data = '';                        }			$hasil->free_result();                        $this->db->close();			return $data;              }           function GeneratePromo($cond = null){		 $query		= "SELECT a.promo_id,a.promo_title, a.promo_active_status,a.promo_desc, "                                    . "DATE_FORMAT( a.promo_create_date, '%d-%m-%Y %H:%i:%s' ) as promo_create_date, a.promo_create_by "                                                                      . "FROM tbl_promo as a "                                    . " ".$cond;		$query		= $this->db->query($query)->result_array();				return $query;	}   }