<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class Model_sectiona extends CI_Model {    function __construct()    {        parent::__construct();    }        function getModule($module_name){            $data = array();            $sql="select module_id , module_group_id from tbl_module where module_path='".$module_name."' ";         		 $hasil = $this->db->query($sql);                        if($hasil->num_rows() > 0){				$data = $hasil->result();			}                        else{                            $data = '';                        }			$hasil->free_result();                        $this->db->close();			return $data;                 }         function getListSectiona($cond = null){		  $query	    = "SELECT  a.sectiona_id,  a.sectiona_title, a.sectiona_image, a.sectiona_desc,"                                    . " a.sectiona_active_status,"                                    . "DATE_FORMAT( a.sectiona_create_date, '%d-%m-%Y %H:%i:%s' ) as sectiona_create_date, a.sectiona_create_by "                                                                      . "FROM tbl_sectiona as a "                                    . " ".$cond;		$query		= $this->db->query($query)->result_array();		return $query;	}         		function getListSectionaAlias(){		 $query		= "SELECT sectiona_id, sectiona_title, sectiona_image, sectiona_desc,						DATE_FORMAT( sectiona_create_date, '%d %M %Y' ) as sectiona_create_date,						c.web_alias, c.web_ori						FROM tbl_sectiona a						INNER JOIN tbl_alias c ON a.sectiona_id = c.key_id 						WHERE a.sectiona_active_status = 1 AND c.alias_category = 'sectiona' AND c.alias_active_status = 1						ORDER BY sectiona_id DESC";		$query		= $this->db->query($query)->result_array();		return $query;	}		function getSectiona($id = '')	{		$where = '';		if($id != ''){			$where = "WHERE sectiona_id = ".$id;		}		 $sql	= "SELECT a.sectiona_id,a.sectiona_title,a.sectiona_image, a.sectiona_active_status,a.sectiona_desc, "                                    . "DATE_FORMAT( a.sectiona_create_date, '%d-%m-%Y %H:%i:%s' ) as sectiona_create_date, a.sectiona_create_by "                                                                      . "FROM tbl_sectiona as a "                                    . " $where "                                    . " ORDER BY sectiona_id ASC";			$query	= $this->db->query($sql);		$rs		= $query->result_array(); 		return $rs;		}	function checkSectiona($sectiona_title){		$sql	= "SELECT sectiona_title FROM tbl_sectiona WHERE sectiona_title = '".$sectiona_title."'";		$query	= $this->db->query($sql);		$rs		= $query->result_array(); 		return $rs;	}	function activeSectiona($id)	{		$sql	= "UPDATE tbl_sectiona SET sectiona_active_status = abs(sectiona_active_status-1),  				   sectiona_update_date = now(), 				   sectiona_update_by = ".$_SESSION['admin_data']['user_id']."				   WHERE sectiona_id = ".$id;			$query	= $this->db->query($sql);				return $query;	}	function deleteSectiona($id = '')	{		$sql	= "DELETE FROM tbl_sectiona WHERE sectiona_id = ".$id;			$query	= $this->db->query($sql);				$str = $this->db->last_query();				return $str;	}        function insertSectiona($sectiona_title,$sectiona_imageurl,$sectiona_desc)	{		$sql	= "INSERT INTO tbl_sectiona SET                             sectiona_title='".$sectiona_title."',                            sectiona_image='".$sectiona_imageurl."',                            sectiona_desc='".$sectiona_desc."',                            sectiona_create_by = ".$_SESSION['admin_data']['user_id'].", sectiona_create_date = now()";					$query  = $this->db->query($sql);		$last_id  = $this->db->insert_id();		return $last_id;	}	function updateSectiona($id,$sectiona_title,$sectiona_imageurl,$sectiona_desc)	{		 $sql	= "UPDATE tbl_sectiona SET                             sectiona_title='".$sectiona_title."',                             sectiona_image='".$sectiona_imageurl."',                            sectiona_desc='".$sectiona_desc."',                            sectiona_update_by = ".$_SESSION['admin_data']['user_id'].",                             sectiona_update_date=now() WHERE sectiona_id = ".$id;			$query	= $this->db->query($sql);		return $query;	}         function updateOrderSectiona($id,$order)	{		$sql	= "UPDATE tbl_sectiona SET 				   sectiona_order= ".$order.",				   sectiona_update_by = ".$_SESSION['admin_data']['user_id'].", sectiona_update_date=now() WHERE sectiona_id = ".$id;			$query	= $this->db->query($sql);				return $query;	}         function getPageView(){         $data = array();         $sql="select * from tbl_page_view order by page_type asc";                  $hasil = $this->db->query($sql);                        if($hasil->num_rows() > 0){				$data = $hasil->result();			}                        else {                            $data = '';                        }			$hasil->free_result();                        $this->db->close();			return $data;     }           function GenerateSectiona($cond = null){            $query		 = "SELECT  a.sectiona_id,  a.sectiona_title, a.sectiona_image, a.sectiona_desc,"                                    . "a.sectiona_active_status,"                                    . "DATE_FORMAT( a.sectiona_create_date, '%d-%m-%Y %H:%i:%s' ) as sectiona_create_date, a.sectiona_create_by "                                                                      . "FROM tbl_sectiona as a "                                    . " ".$cond;                    $query = $this->db->query($query)->result_array();                    $data  = array();                    foreach ($query as $row) {                    $data[] = array(                    'sectiona_id' => $row['sectiona_id'],                    'sectiona_title' => $row['sectiona_title'],                    'sectiona_image' => $row['sectiona_image'],                    'sectiona_desc' => $row['sectiona_desc'],                    'gallery' => $this->getGallery($row['sectiona_id'])                );            }            //$query->free_result();            $this->db->close();            return $data;	} function getGallery($sectiona_id){		 $query	 = "SELECT a.sectiona_gallery_id, a.sectiona_gallery_title,a.sectiona_gallery_image,"                                    . " a.sectiona_gallery_active_status,"                                    . "a.sectiona_gallery_order,"                                    . "DATE_FORMAT( a.sectiona_gallery_create_date, '%d-%m-%Y %H:%i:%s' ) as sectiona_gallery_create_date, a.sectiona_gallery_create_by "                                    . "FROM tbl_sectiona_gallery as a "                                    . " inner join tbl_sectiona as b on a.sectiona_id = b.sectiona_id "                                    . " where a.sectiona_gallery_active_status =1 and a.sectiona_id =".$sectiona_id." order by a.sectiona_gallery_order ASC";                                    		$query		= $this->db->query($query)->result_array();		return $query;	}      function getListBanner($cond = null){		  $query	    = "SELECT  a.banner_sectiona_id,  a.banner_sectiona_title,"                                    . " a.banner_sectiona_image, a.banner_sectiona_order, a.banner_sectiona_active_status,"                                    . "DATE_FORMAT( a.banner_sectiona_create_date, '%d-%m-%Y %H:%i:%s' ) as banner_sectiona_create_date, a.banner_sectiona_create_by "                                                                      . "FROM tbl_banner_sectiona as a "                                    . " ".$cond;		$query		= $this->db->query($query)->result_array();		return $query;	}  function getBanner($id = '')	{		$where = '';		if($id != ''){			$where = "WHERE banner_sectiona_id = ".$id;		}		 $sql	= "SELECT a.banner_sectiona_id,a.banner_sectiona_title,a.banner_sectiona_image, a.banner_sectiona_active_status, "                                    . "DATE_FORMAT( a.banner_sectiona_create_date, '%d-%m-%Y %H:%i:%s' ) as banner_sectiona_create_date, a.banner_sectiona_create_by "                                                                      . "FROM tbl_banner_sectiona as a "                                    . " $where "                                    . " ORDER BY banner_sectiona_id ASC";			$query	= $this->db->query($sql);		$rs		= $query->result_array(); 		return $rs;		}	function checkBanner($banner_sectiona_title){		$sql	= "SELECT banner_sectiona_title FROM tbl_banner_sectiona WHERE banner_sectiona_title = '".$banner_sectiona_title."'";		$query	= $this->db->query($sql);		$rs		= $query->result_array(); 		return $rs;	}	function activeBanner($id)	{		$sql	= "UPDATE tbl_banner_sectiona SET banner_sectiona_active_status = abs(banner_sectiona_active_status-1),  				   banner_sectiona_update_date = now(), 				   banner_sectiona_update_by = ".$_SESSION['admin_data']['user_id']."				   WHERE banner_sectiona_id = ".$id;			$query	= $this->db->query($sql);				return $query;	}	function deleteBanner($id = '')	{		$sql	= "DELETE FROM tbl_banner_sectiona WHERE banner_sectiona_id = ".$id;			$query	= $this->db->query($sql);				$str = $this->db->last_query();				return $str;	}        function insertBanner($banner_sectiona_title,$banner_sectiona_image)	{		$sql	= "INSERT INTO tbl_banner_sectiona SET                             banner_sectiona_title='".$banner_sectiona_title."',                                banner_sectiona_image='".$banner_sectiona_image."',                             banner_sectiona_create_by = ".$_SESSION['admin_data']['user_id'].", banner_sectiona_create_date = now()";					$query  = $this->db->query($sql);		$last_id  = $this->db->insert_id();		return $last_id;	}	function updateBanner($id,$banner_sectiona_title,$banner_sectiona_image,$banner_sectiona_status)	{		 $sql	= "UPDATE tbl_banner_sectiona SET                             banner_sectiona_title='".$banner_sectiona_title."',                             banner_sectiona_image='".$banner_sectiona_image."',                             banner_sectiona_active_status='".$banner_sectiona_status."',                            banner_sectiona_update_by = ".$_SESSION['admin_data']['user_id'].",                            banner_sectiona_update_date=now() WHERE banner_sectiona_id = ".$id;			$query	= $this->db->query($sql);		return $query;	}         function updateOrderBanner($id,$order)	{		$sql	= "UPDATE tbl_banner_sectiona SET 				   banner_sectiona_order= ".$order.",				   banner_sectiona_update_by = ".$_SESSION['admin_data']['user_id'].", banner_sectiona_update_date=now() WHERE banner_sectiona_id = ".$id;			$query	= $this->db->query($sql);				return $query;	}   }