<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class Model_sectiona_gallery extends CI_Model {    function __construct()    {        parent::__construct();    }		function getGallery($sectiona_id,$cond = null){		 $query	 = "SELECT a.sectiona_gallery_id, a.sectiona_gallery_title,a.sectiona_gallery_image,"                                    . " a.sectiona_gallery_active_status,"                                    . "a.sectiona_gallery_order,"                                    . "DATE_FORMAT( a.sectiona_gallery_create_date, '%d-%m-%Y %H:%i:%s' ) as sectiona_gallery_create_date, a.sectiona_gallery_create_by "                                    . "FROM tbl_sectiona_gallery as a "                                    . " inner join tbl_sectiona as b on a.sectiona_id = b.sectiona_id "                                    . " where a.sectiona_id=".$sectiona_id." "                                    . " ".$cond;		$query		= $this->db->query($query)->result_array();		return $query;	}	    function getTitle($sectiona_gallery_id){            $data = array();             $hasil =   $this->db->query("select material_title as title from tbl_sectiona_gallery where sectiona_gallery_id = $sectiona_gallery_id ");				$data = $hasil->row_array();                                               return $data['title'];         }		function getGalleryBy($id = '')	{		$where = '';		if($id != ''){			$where = "WHERE sectiona_gallery_id = ".$id;		}		$sql	= "SELECT a.sectiona_gallery_id, a.sectiona_gallery_title,a.sectiona_gallery_image,"                                    . "  a.sectiona_gallery_active_status,"                                    . "a.sectiona_gallery_order,"                                    . "DATE_FORMAT( a.sectiona_gallery_create_date, '%d-%m-%Y %H:%i:%s' ) as sectiona_gallery_create_date, a.sectiona_gallery_create_by, "                                    . " b.sectiona_id "                                    . "FROM tbl_sectiona_gallery as a "                                    . " inner join tbl_sectiona as b on a.sectiona_id = b.sectiona_id "                                    . " $where "                                    . " ORDER BY sectiona_gallery_id ASC";			$query	= $this->db->query($sql);		$rs		= $query->result_array(); 				return $rs;		}		function activeGallery($id)	{		 $sql	= "UPDATE tbl_sectiona_gallery SET sectiona_gallery_active_status = abs(sectiona_gallery_active_status-1),  				   sectiona_gallery_update_date = now(), 				   sectiona_gallery_update_by = ".$_SESSION['admin_data']['user_id']."				   WHERE sectiona_gallery_id = ".$id;			$query	= $this->db->query($sql);				return $query;	}		function deleteGallery($id = '')	{		$sql	= "DELETE FROM tbl_sectiona_gallery WHERE sectiona_id = ".$id;			$query	= $this->db->query($sql);				$str = $this->db->last_query();				return $str;	}	function deleteGalleryby($id = '')	{		$sql	= "DELETE FROM tbl_sectiona_gallery WHERE sectiona_gallery_id = ".$id;			$query	= $this->db->query($sql);				$str = $this->db->last_query();				return $str;	}	function checkGallery($sectiona_gallery_title, $sectiona_id){		$sql	= "SELECT sectiona_gallery_title FROM tbl_sectiona_gallery WHERE sectiona_gallery_title = '".$sectiona_gallery_title."' and sectiona_id = '".$sectiona_id."'";		$query	= $this->db->query($sql);		$rs		= $query->result_array(); 		return $rs;	}	            function insertGallery($data){                foreach ($data as $value) {                  $this->db->insert('tbl_sectiona_gallery', $value);                   }                $this->db->close();	}       	function editGallery($sectiona_gallery_title,$sectiona_gallery_image,$sectiona_gallery_order,$sectiona_gallery_status,$sectiona_gallery_id){          $today= date("Y-m-d H:i:s");          $data = array(                        'sectiona_gallery_title'=>$sectiona_gallery_title,                        'sectiona_gallery_image'=>$sectiona_gallery_image,                        'sectiona_gallery_order'=>$sectiona_gallery_order,                        'sectiona_gallery_active_status'=>$sectiona_gallery_status,                        'sectiona_gallery_update_by'=> $_SESSION['admin_data']['user_id'],                         'sectiona_gallery_update_date'=> $today                        );            $this->db->where('sectiona_gallery_id', $sectiona_gallery_id);            $this->db->update('tbl_sectiona_gallery', $data);            $this->db->close();	        }     function updateOrderGallery($id,$order)	{		$sql	= "UPDATE tbl_sectiona_gallery SET 				   sectiona_gallery_order= ".$order.",				   sectiona_gallery_update_by = ".$_SESSION['admin_data']['user_id'].", sectiona_gallery_update_date=now() WHERE sectiona_gallery_id = ".$id;			$query	= $this->db->query($sql);		return $query;	}      function generateSectiona(){		$query	= "SELECT a.sectiona_gallery_id, a.sectiona_gallery_title,a.sectiona_gallery_image,"                                    . "  a.sectiona_gallery_active_status,"                                    . "a.sectiona_gallery_order,"                                    . "DATE_FORMAT( a.sectiona_gallery_create_date, '%d-%m-%Y %H:%i:%s' ) as sectiona_gallery_create_date, a.sectiona_gallery_create_by, "                                    . " b.sectiona_id "                                    . "FROM tbl_sectiona_gallery as a "                                    . " inner join tbl_sectiona as b on a.sectiona_id = b.sectiona_id "                                    . " where b.sectiona_active_status=1 and a.sectiona_gallery_active_status=1 "                                    . " ORDER BY sectiona_gallery_id ASC";		$query		= $this->db->query($query)->result_array();				return $query;	}        }