<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class Model_sectionb_gallery extends CI_Model {    function __construct()    {        parent::__construct();    }		function getGallery($sectionb_id,$cond = null){		 $query	 = "SELECT a.sectionb_gallery_id, a.sectionb_gallery_title,a.sectionb_gallery_image,"                                    . " a.sectionb_gallery_active_status,"                                    . "a.sectionb_gallery_order,"                                    . "DATE_FORMAT( a.sectionb_gallery_create_date, '%d-%m-%Y %H:%i:%s' ) as sectionb_gallery_create_date, a.sectionb_gallery_create_by "                                    . "FROM tbl_sectionb_gallery as a "                                    . " inner join tbl_sectionb as b on a.sectionb_id = b.sectionb_id "                                    . " where a.sectionb_id=".$sectionb_id." "                                    . " ".$cond;		$query		= $this->db->query($query)->result_array();		return $query;	}	    function getTitle($sectionb_gallery_id){            $data = array();             $hasil =   $this->db->query("select material_title as title from tbl_sectionb_gallery where sectionb_gallery_id = $sectionb_gallery_id ");				$data = $hasil->row_array();                                               return $data['title'];         }		function getGalleryBy($id = '')	{		$where = '';		if($id != ''){			$where = "WHERE sectionb_gallery_id = ".$id;		}		$sql	= "SELECT a.sectionb_gallery_id, a.sectionb_gallery_title,a.sectionb_gallery_image,"                                    . "  a.sectionb_gallery_active_status,"                                    . "a.sectionb_gallery_order,"                                    . "DATE_FORMAT( a.sectionb_gallery_create_date, '%d-%m-%Y %H:%i:%s' ) as sectionb_gallery_create_date, a.sectionb_gallery_create_by, "                                    . " b.sectionb_id "                                    . "FROM tbl_sectionb_gallery as a "                                    . " inner join tbl_sectionb as b on a.sectionb_id = b.sectionb_id "                                    . " $where "                                    . " ORDER BY sectionb_gallery_id ASC";			$query	= $this->db->query($sql);		$rs		= $query->result_array(); 				return $rs;		}		function activeGallery($id)	{		 $sql	= "UPDATE tbl_sectionb_gallery SET sectionb_gallery_active_status = abs(sectionb_gallery_active_status-1),  				   sectionb_gallery_update_date = now(), 				   sectionb_gallery_update_by = ".$_SESSION['admin_data']['user_id']."				   WHERE sectionb_gallery_id = ".$id;			$query	= $this->db->query($sql);				return $query;	}		function deleteGallery($id = '')	{		$sql	= "DELETE FROM tbl_sectionb_gallery WHERE sectionb_id = ".$id;			$query	= $this->db->query($sql);				$str = $this->db->last_query();				return $str;	}	function deleteGalleryby($id = '')	{		$sql	= "DELETE FROM tbl_sectionb_gallery WHERE sectionb_gallery_id = ".$id;			$query	= $this->db->query($sql);				$str = $this->db->last_query();				return $str;	}	function checkGallery($sectionb_gallery_title, $sectionb_id){		$sql	= "SELECT sectionb_gallery_title FROM tbl_sectionb_gallery WHERE sectionb_gallery_title = '".$sectionb_gallery_title."' and sectionb_id = '".$sectionb_id."'";		$query	= $this->db->query($sql);		$rs		= $query->result_array(); 		return $rs;	}	            function insertGallery($data){                foreach ($data as $value) {                  $this->db->insert('tbl_sectionb_gallery', $value);                   }                $this->db->close();	}       	function editGallery($sectionb_gallery_title,$sectionb_gallery_image,$sectionb_gallery_order,$sectionb_gallery_status,$sectionb_gallery_id){          $today= date("Y-m-d H:i:s");          $data = array(                        'sectionb_gallery_title'=>$sectionb_gallery_title,                        'sectionb_gallery_image'=>$sectionb_gallery_image,                        'sectionb_gallery_order'=>$sectionb_gallery_order,                        'sectionb_gallery_active_status'=>$sectionb_gallery_status,                        'sectionb_gallery_update_by'=> $_SESSION['admin_data']['user_id'],                         'sectionb_gallery_update_date'=> $today                        );            $this->db->where('sectionb_gallery_id', $sectionb_gallery_id);            $this->db->update('tbl_sectionb_gallery', $data);            $this->db->close();	        }     function updateOrderGallery($id,$order)	{		$sql	= "UPDATE tbl_sectionb_gallery SET 				   sectionb_gallery_order= ".$order.",				   sectionb_gallery_update_by = ".$_SESSION['admin_data']['user_id'].", sectionb_gallery_update_date=now() WHERE sectionb_gallery_id = ".$id;			$query	= $this->db->query($sql);		return $query;	}      function generateSectionb(){		$query	= "SELECT a.sectionb_gallery_id, a.sectionb_gallery_title,a.sectionb_gallery_image,"                                    . "  a.sectionb_gallery_active_status,"                                    . "a.sectionb_gallery_order,"                                    . "DATE_FORMAT( a.sectionb_gallery_create_date, '%d-%m-%Y %H:%i:%s' ) as sectionb_gallery_create_date, a.sectionb_gallery_create_by, "                                    . " b.sectionb_id "                                    . "FROM tbl_sectionb_gallery as a "                                    . " inner join tbl_sectionb as b on a.sectionb_id = b.sectionb_id "                                    . " where b.sectionb_active_status=1 and a.sectionb_gallery_active_status=1 "                                    . " ORDER BY sectionb_gallery_id ASC";		$query		= $this->db->query($query)->result_array();				return $query;	}        }