<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class Model_sectione extends CI_Model {    function __construct()    {        parent::__construct();    }        function getModule($module_name){            $data = array();            $sql="select module_id , module_group_id from tbl_module where module_path='".$module_name."' ";         		 $hasil = $this->db->query($sql);                        if($hasil->num_rows() > 0){				$data = $hasil->result();			}                        else{                            $data = '';                        }			$hasil->free_result();                        $this->db->close();			return $data;                 }         function getListSectione($cond = null){		  $query	    = "SELECT  a.sectione_id,  a.sectione_title, a.sectione_image, a.sectione_desc,"                                    . " a.sectione_image, a.sectione_active_status,"                                    . "DATE_FORMAT( a.sectione_create_date, '%d-%m-%Y %H:%i:%s' ) as sectione_create_date, a.sectione_create_by "                                                                      . "FROM tbl_sectione as a "                                    . " ".$cond;		$query		= $this->db->query($query)->result_array();		return $query;	}         		function getListSectioneAlias(){		 $query		= "SELECT sectione_id, sectione_title, sectione_image, sectione_desc,						DATE_FORMAT( sectione_create_date, '%d %M %Y' ) as sectione_create_date,						c.web_alias, c.web_ori						FROM tbl_sectione a						INNER JOIN tbl_alias c ON a.sectione_id = c.key_id 						WHERE a.sectione_active_status = 1 AND c.alias_category = 'sectione' AND c.alias_active_status = 1						ORDER BY sectione_id DESC";		$query		= $this->db->query($query)->result_array();		return $query;	}		function getSectione($id = '')	{		$where = '';		if($id != ''){			$where = "WHERE sectione_id = ".$id;		}		 $sql	= "SELECT a.sectione_id,a.sectione_title,a.sectione_image, a.sectione_active_status,a.sectione_desc, a.sectione_urla,a.sectione_urlb,a.sectione_urlc,"                                    . "DATE_FORMAT( a.sectione_create_date, '%d-%m-%Y %H:%i:%s' ) as sectione_create_date, a.sectione_create_by "                                                                      . "FROM tbl_sectione as a "                                    . " $where "                                    . " ORDER BY sectione_id ASC";			$query	= $this->db->query($sql);		$rs		= $query->result_array(); 		return $rs;		}	function checkSectione($sectione_title){		$sql	= "SELECT sectione_title FROM tbl_sectione WHERE sectione_title = '".$sectione_title."'";		$query	= $this->db->query($sql);		$rs		= $query->result_array(); 		return $rs;	}	function activeSectione($id)	{		$sql	= "UPDATE tbl_sectione SET sectione_active_status = abs(sectione_active_status-1),  				   sectione_update_date = now(), 				   sectione_update_by = ".$_SESSION['admin_data']['user_id']."				   WHERE sectione_id = ".$id;			$query	= $this->db->query($sql);				return $query;	}	function deleteSectione($id = '')	{		$sql	= "DELETE FROM tbl_sectione WHERE sectione_id = ".$id;			$query	= $this->db->query($sql);				$str = $this->db->last_query();				return $str;	}        function insertSectione($sectione_title,$sectione_imageurl,$sectione_desc,$sectione_urla,$sectione_urlb,$sectione_urlc)	{		$sql	= "INSERT INTO tbl_sectione SET                             sectione_title='".$sectione_title."',                            sectione_image='".$sectione_imageurl."',                            sectione_desc='".$sectione_desc."',                            sectione_urla='".$sectione_urla."',                            sectione_urlb='".$sectione_urlb."',                            sectione_urlc='".$sectione_urlc."',                            sectione_create_by = ".$_SESSION['admin_data']['user_id'].", sectione_create_date = now()";					$query  = $this->db->query($sql);		$last_id  = $this->db->insert_id();		return $last_id;	}	function updateSectione($id,$sectione_title,$sectione_imageurl,$sectione_desc,$sectione_urla,$sectione_urlb,$sectione_urlc)	{		 $sql	= "UPDATE tbl_sectione SET                             sectione_title='".$sectione_title."',                             sectione_image='".$sectione_imageurl."',                            sectione_desc='".$sectione_desc."',                            sectione_urla='".$sectione_urla."',                            sectione_urlb='".$sectione_urlb."',                            sectione_urlc='".$sectione_urlc."',                            sectione_update_by = ".$_SESSION['admin_data']['user_id'].",                             sectione_update_date=now() WHERE sectione_id = ".$id;			$query	= $this->db->query($sql);		return $query;	}         function updateOrderSectione($id,$order)	{		$sql	= "UPDATE tbl_sectione SET 				   sectione_order= ".$order.",				   sectione_update_by = ".$_SESSION['admin_data']['user_id'].", sectione_update_date=now() WHERE sectione_id = ".$id;			$query	= $this->db->query($sql);				return $query;	}         function getPageView(){         $data = array();         $sql="select * from tbl_page_view order by page_type asc";                  $hasil = $this->db->query($sql);                        if($hasil->num_rows() > 0){				$data = $hasil->result();			}                        else{                            $data = '';                        }			$hasil->free_result();                        $this->db->close();			return $data;              }        function GenerateSectione($cond = null){            $query		 = "SELECT  a.sectione_id,  a.sectione_title, a.sectione_image, a.sectione_desc, a.sectione_urla,a.sectione_urlb,a.sectione_urlc,"                                    . "a.sectione_active_status,"                                    . "DATE_FORMAT( a.sectione_create_date, '%d-%m-%Y %H:%i:%s' ) as sectione_create_date, a.sectione_create_by "                                                                      . "FROM tbl_sectione as a "                                    . " ".$cond;                    $query = $this->db->query($query)->result_array();                    $data  = array();                    foreach ($query as $row) {                    $data[] = array(                    'sectione_id' => $row['sectione_id'],                    'sectione_title' => $row['sectione_title'],                    'sectione_image' => $row['sectione_image'],                    'sectione_desc' => $row['sectione_desc'],                    'sectione_urla' => $row['sectione_urla'],                    'sectione_urlb' => $row['sectione_urlb'],                    'sectione_urlc' => $row['sectione_urlc'],                    'gallery' => $this->getGallery($row['sectione_id'])                );            }            //$query->free_result();            $this->db->close();            return $data;	} function getGallery($sectione_id){		 $query	 = "SELECT a.sectione_gallery_id, a.sectione_gallery_title,a.sectione_gallery_image,"                                    . " a.sectione_gallery_active_status,"                                    . "a.sectione_gallery_order,"                                    . "DATE_FORMAT( a.sectione_gallery_create_date, '%d-%m-%Y %H:%i:%s' ) as sectione_gallery_create_date, a.sectione_gallery_create_by "                                    . "FROM tbl_sectione_gallery as a "                                    . " inner join tbl_sectione as b on a.sectione_id = b.sectione_id "                                    . " where a.sectione_gallery_active_status =1 and a.sectione_id =".$sectione_id." order by a.sectione_gallery_order ASC";                                    		$query		= $this->db->query($query)->result_array();		return $query;	}       function getListBanner($cond = null){		  $query	    = "SELECT  a.banner_sectione_id,  a.banner_sectione_title,"                                    . " a.banner_sectione_image, a.banner_sectione_order, a.banner_sectione_active_status,"                                    . "DATE_FORMAT( a.banner_sectione_create_date, '%d-%m-%Y %H:%i:%s' ) as banner_sectione_create_date, a.banner_sectione_create_by "                                                                      . "FROM tbl_banner_sectione as a "                                    . " ".$cond;		$query		= $this->db->query($query)->result_array();		return $query;	}  function getBanner($id = '')	{		$where = '';		if($id != ''){			$where = "WHERE banner_sectione_id = ".$id;		}		 $sql	= "SELECT a.banner_sectione_id,a.banner_sectione_title,a.banner_sectione_image, a.banner_sectione_active_status, "                                    . "DATE_FORMAT( a.banner_sectione_create_date, '%d-%m-%Y %H:%i:%s' ) as banner_sectione_create_date, a.banner_sectione_create_by "                                                                      . "FROM tbl_banner_sectione as a "                                    . " $where "                                    . " ORDER BY banner_sectione_id ASC";			$query	= $this->db->query($sql);		$rs		= $query->result_array(); 		return $rs;		}	function checkBanner($banner_sectione_title){		$sql	= "SELECT banner_sectione_title FROM tbl_banner_sectione WHERE banner_sectione_title = '".$banner_sectione_title."'";		$query	= $this->db->query($sql);		$rs		= $query->result_array(); 		return $rs;	}	function activeBanner($id)	{		$sql	= "UPDATE tbl_banner_sectione SET banner_sectione_active_status = abs(banner_sectione_active_status-1),  				   banner_sectione_update_date = now(), 				   banner_sectione_update_by = ".$_SESSION['admin_data']['user_id']."				   WHERE banner_sectione_id = ".$id;			$query	= $this->db->query($sql);				return $query;	}	function deleteBanner($id = '')	{		$sql	= "DELETE FROM tbl_banner_sectione WHERE banner_sectione_id = ".$id;			$query	= $this->db->query($sql);				$str = $this->db->last_query();				return $str;	}        function insertBanner($banner_sectione_title,$banner_sectione_image)	{		$sql	= "INSERT INTO tbl_banner_sectione SET                             banner_sectione_title='".$banner_sectione_title."',                                banner_sectione_image='".$banner_sectione_image."',                             banner_sectione_create_by = ".$_SESSION['admin_data']['user_id'].", banner_sectione_create_date = now()";					$query  = $this->db->query($sql);		$last_id  = $this->db->insert_id();		return $last_id;	}	function updateBanner($id,$banner_sectione_title,$banner_sectione_image,$banner_sectione_status)	{		 $sql	= "UPDATE tbl_banner_sectione SET                             banner_sectione_title='".$banner_sectione_title."',                             banner_sectione_image='".$banner_sectione_image."',                             banner_sectione_active_status='".$banner_sectione_status."',                            banner_sectione_update_by = ".$_SESSION['admin_data']['user_id'].",                            banner_sectione_update_date=now() WHERE banner_sectione_id = ".$id;			$query	= $this->db->query($sql);		return $query;	}         function updateOrderBanner($id,$order)	{		$sql	= "UPDATE tbl_banner_sectione SET 				   banner_sectione_order= ".$order.",				   banner_sectione_update_by = ".$_SESSION['admin_data']['user_id'].", banner_sectione_update_date=now() WHERE banner_sectione_id = ".$id;			$query	= $this->db->query($sql);				return $query;	}   }