<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class Model_sectione_gallery extends CI_Model {    function __construct()    {        parent::__construct();    }		function getGallery($sectione_id,$cond = null){		 $query	 = "SELECT a.sectione_gallery_id, a.sectione_gallery_title,a.sectione_gallery_image,"                                    . " a.sectione_gallery_active_status,"                                    . "a.sectione_gallery_order,"                                    . "DATE_FORMAT( a.sectione_gallery_create_date, '%d-%m-%Y %H:%i:%s' ) as sectione_gallery_create_date, a.sectione_gallery_create_by "                                    . "FROM tbl_sectione_gallery as a "                                    . " inner join tbl_sectione as b on a.sectione_id = b.sectione_id "                                    . " where a.sectione_id=".$sectione_id." "                                    . " ".$cond;		$query		= $this->db->query($query)->result_array();		return $query;	}	    function getTitle($sectione_gallery_id){            $data = array();             $hasil =   $this->db->query("select material_title as title from tbl_sectione_gallery where sectione_gallery_id = $sectione_gallery_id ");				$data = $hasil->row_array();                                               return $data['title'];         }		function getGalleryBy($id = '')	{		$where = '';		if($id != ''){			$where = "WHERE sectione_gallery_id = ".$id;		}		$sql	= "SELECT a.sectione_gallery_id, a.sectione_gallery_title,a.sectione_gallery_image,"                                    . "  a.sectione_gallery_active_status,"                                    . "a.sectione_gallery_order,"                                    . "DATE_FORMAT( a.sectione_gallery_create_date, '%d-%m-%Y %H:%i:%s' ) as sectione_gallery_create_date, a.sectione_gallery_create_by, "                                    . " b.sectione_id "                                    . "FROM tbl_sectione_gallery as a "                                    . " inner join tbl_sectione as b on a.sectione_id = b.sectione_id "                                    . " $where "                                    . " ORDER BY sectione_gallery_id ASC";			$query	= $this->db->query($sql);		$rs		= $query->result_array(); 				return $rs;		}		function activeGallery($id)	{		 $sql	= "UPDATE tbl_sectione_gallery SET sectione_gallery_active_status = abs(sectione_gallery_active_status-1),  				   sectione_gallery_update_date = now(), 				   sectione_gallery_update_by = ".$_SESSION['admin_data']['user_id']."				   WHERE sectione_gallery_id = ".$id;			$query	= $this->db->query($sql);				return $query;	}		function deleteGallery($id = '')	{		$sql	= "DELETE FROM tbl_sectione_gallery WHERE sectione_id = ".$id;			$query	= $this->db->query($sql);				$str = $this->db->last_query();				return $str;	}	function deleteGalleryby($id = '')	{		$sql	= "DELETE FROM tbl_sectione_gallery WHERE sectione_gallery_id = ".$id;			$query	= $this->db->query($sql);				$str = $this->db->last_query();				return $str;	}	function checkGallery($sectione_gallery_title, $sectione_id){		$sql	= "SELECT sectione_gallery_title FROM tbl_sectione_gallery WHERE sectione_gallery_title = '".$sectione_gallery_title."' and sectione_id = '".$sectione_id."'";		$query	= $this->db->query($sql);		$rs		= $query->result_array(); 		return $rs;	}	            function insertGallery($data){                foreach ($data as $value) {                  $this->db->insert('tbl_sectione_gallery', $value);                   }                $this->db->close();	}       	function editGallery($sectione_gallery_title,$sectione_gallery_image,$sectione_gallery_order,$sectione_gallery_status,$sectione_gallery_id){          $today= date("Y-m-d H:i:s");          $data = array(                        'sectione_gallery_title'=>$sectione_gallery_title,                        'sectione_gallery_image'=>$sectione_gallery_image,                        'sectione_gallery_order'=>$sectione_gallery_order,                        'sectione_gallery_active_status'=>$sectione_gallery_status,                        'sectione_gallery_update_by'=> $_SESSION['admin_data']['user_id'],                         'sectione_gallery_update_date'=> $today                        );            $this->db->where('sectione_gallery_id', $sectione_gallery_id);            $this->db->update('tbl_sectione_gallery', $data);            $this->db->close();	        }     function updateOrderGallery($id,$order)	{		$sql	= "UPDATE tbl_sectione_gallery SET 				   sectione_gallery_order= ".$order.",				   sectione_gallery_update_by = ".$_SESSION['admin_data']['user_id'].", sectione_gallery_update_date=now() WHERE sectione_gallery_id = ".$id;			$query	= $this->db->query($sql);		return $query;	}      function generateSectione(){		$query	= "SELECT a.sectione_gallery_id, a.sectione_gallery_title,a.sectione_gallery_image,"                                    . "  a.sectione_gallery_active_status,"                                    . "a.sectione_gallery_order,"                                    . "DATE_FORMAT( a.sectione_gallery_create_date, '%d-%m-%Y %H:%i:%s' ) as sectione_gallery_create_date, a.sectione_gallery_create_by, "                                    . " b.sectione_id "                                    . "FROM tbl_sectione_gallery as a "                                    . " inner join tbl_sectione as b on a.sectione_id = b.sectione_id "                                    . " where b.sectione_active_status=1 and a.sectione_gallery_active_status=1 "                                    . " ORDER BY sectione_gallery_id ASC";		$query		= $this->db->query($query)->result_array();				return $query;	}        }