<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class Model_special extends CI_Model {    function __construct()    {        parent::__construct();    }        function getModule($module_name){            $data = array();            $sql="select module_id , module_group_id from tbl_module where module_path='".$module_name."' ";         		 $hasil = $this->db->query($sql);                        if($hasil->num_rows() > 0){				$data = $hasil->result();			}                        else{                            $data = '';                        }			$hasil->free_result();                        $this->db->close();			return $data;                 }         function getListSpecial($cond = null){		  $query	    = "SELECT  a.special_id,  a.special_title, a.special_image, a.special_desc,a.special_order,"                                    . " a.special_active_status,"                                    . "DATE_FORMAT( a.special_create_date, '%d-%m-%Y %H:%i:%s' ) as special_create_date, a.special_create_by "                                                                      . "FROM tbl_special as a "                                    . " ".$cond;		$query		= $this->db->query($query)->result_array();		return $query;	}         		function getListSpecialAlias(){		 $query		= "SELECT special_id, special_title, special_image, special_desc,						DATE_FORMAT( special_create_date, '%d %M %Y' ) as special_create_date,						c.web_alias, c.web_ori						FROM tbl_special a						INNER JOIN tbl_alias c ON a.special_id = c.key_id 						WHERE a.special_active_status = 1 AND c.alias_category = 'special' AND c.alias_active_status = 1						ORDER BY special_id DESC";		$query		= $this->db->query($query)->result_array();		return $query;	}		function getSpecial($id = '')	{		$where = '';		if($id != ''){			$where = "WHERE special_id = ".$id;		}		 $sql	= "SELECT a.special_id,a.special_title,a.special_name,a.special_image, a.special_active_status,a.special_desc,a.special_urla, "                                    . "DATE_FORMAT( a.special_create_date, '%d-%m-%Y %H:%i:%s' ) as special_create_date, a.special_create_by "                                                                      . "FROM tbl_special as a "                                    . " $where "                                    . " ORDER BY special_id ASC";			$query	= $this->db->query($sql);		$rs		= $query->result_array(); 		return $rs;		}	function checkSpecial($special_title){		$sql	= "SELECT special_title FROM tbl_special WHERE special_title = '".$special_title."'";		$query	= $this->db->query($sql);		$rs		= $query->result_array(); 		return $rs;	}	function activeSpecial($id)	{		$sql	= "UPDATE tbl_special SET special_active_status = abs(special_active_status-1),  				   special_update_date = now(), 				   special_update_by = ".$_SESSION['admin_data']['user_id']."				   WHERE special_id = ".$id;			$query	= $this->db->query($sql);				return $query;	}	function deleteSpecial($id = '')	{		$sql	= "DELETE FROM tbl_special WHERE special_id = ".$id;			$query	= $this->db->query($sql);				$str = $this->db->last_query();				return $str;	}        function insertSpecial($special_title,$special_name, $special_imageurl,$special_desc,$special_urla)	{		$sql	= "INSERT INTO tbl_special SET                             special_title='".$special_title."',                            special_name='".$special_name."',                            special_image='".$special_imageurl."',                            special_desc='".$special_desc."',                            special_urla='".$special_urla."',                            special_create_by = ".$_SESSION['admin_data']['user_id'].", special_create_date = now()";					$query  = $this->db->query($sql);		$last_id  = $this->db->insert_id();		return $last_id;	}	function updateSpecial($id,$special_title,$special_name, $special_imageurl,$special_desc,$special_urla)	{		 $sql	= "UPDATE tbl_special SET                             special_title='".$special_title."',                            special_name='".$special_name."',                            special_image='".$special_imageurl."',                            special_desc='".$special_desc."',                            special_urla='".$special_urla."',                            special_update_by = ".$_SESSION['admin_data']['user_id'].",                             special_update_date=now() WHERE special_id = ".$id;			$query	= $this->db->query($sql);		return $query;	}         function updateOrderSpecial($id,$order)	{		$sql	= "UPDATE tbl_special SET 				   special_order= ".$order.",				   special_update_by = ".$_SESSION['admin_data']['user_id'].", special_update_date=now() WHERE special_id = ".$id;			$query	= $this->db->query($sql);				return $query;	}         function getPageView(){         $data = array();         $sql="select * from tbl_page_view order by page_type asc";                  $hasil = $this->db->query($sql);                        if($hasil->num_rows() > 0){				$data = $hasil->result();			}                        else{                            $data = '';                        }			$hasil->free_result();                        $this->db->close();			return $data;              }          function GenerateSpecial($cond = null){            $query		 = "SELECT  a.special_id,  a.special_title, a.special_name, a.special_image, a.special_desc,a.special_order, a.special_urla,"                                    . "a.special_active_status "                                                                    . "FROM tbl_special as a "                                    . " ".$cond;                    $query = $this->db->query($query)->result_array();                    $data  = array();                    foreach ($query as $row) {                    $data[] = array(                    'special_id' => $row['special_id'],                    'special_title' => $row['special_title'],                    'special_name' => $row['special_name'],                    'special_image' => $row['special_image'],                    'special_desc' => $row['special_desc'],                    'special_order' => $row['special_order'],                    'special_urla' => $row['special_urla']                );            }            //$query->free_result();            $this->db->close();            return $data;	}      }